mapper.select:
SELECT tp.title AS title, tp.doc_code AS docCode, tp.area_name AS areaName, tp.doc_source AS docSource, tp.tax_categories_name AS taxCategoriesName, tp.taxpayer_identify_type_names AS taxpayerIdentifyTypeNames, tp.enterprise_type_names AS enterpriseTypeCodes, tp.industry_names AS industryNames, tp.validity AS validity, tp.release_date AS releaseDate, tp.digest AS digest, tp.content AS content, tp.labels AS labels, tp.policies_status AS policiesStatus, tp.abolish_note AS abolishNote, tp.input_user_id AS inputUserId, tp.create_time AS createTime, tp.update_time AS updateTime FROM t_policies tp
and tp.tax_categories_code= #{queryPoliciesDTO.taxCategoriesCode}
and tp.area_code=#{queryPoliciesDTO.areaCode}
and tp.validity=#{queryPoliciesDTO.validity}
and tp.release_date >= #{queryPoliciesDTO.startTime}
and tp.release_date <= #{queryPoliciesDTO.endTime}
and tp.title like "%"#{queryPoliciesDTO.title}"%"
and tp.doc_code like "%"#{queryPoliciesDTO.docCode}"%"
and FIND_IN_SET(#{taxpayer},tp.taxpayer_identify_type_codes)
and FIND_IN_SET(#{industryCodes},tp.industry_codes)
and FIND_IN_SET(#{enterpriseTypeCodes},tp.enterprise_type_codes)
and tp.deleted=0
order by #{sort}
